<script src="{{ admin_asset("vendor/laravel-admin/AdminLTE/plugins/jQuery/jQuery-2.1.4.min.js")}}"></script>
<script src="{{ admin_asset("vendor/laravel-admin/AdminLTE/bootstrap/js/bootstrap.min.js")}}"></script>
<script>
    var autoMoveInterval;
    var autoTsqInterval;
    var loadingBoxModal;
    showLoading = function(msg){
        $("#loading_box .mvTxt").text(msg);
        $('#loading_box').modal({backdrop: 'static', keyboard: false});
        $("#loading_box").css("z-index",9999);
        var delVal=50;
        function autoMove(){
            delVal++;
            if(delVal>400){
                delVal=50;
            }
            $(".mvBtn").css("left",delVal);
        }
        autoMoveInterval = setInterval(autoMove,8);
        var deNum=0;
        function autoTsq(){
            $(".mvSq").css("color","#F5FAFD");
            setTimeout(function(){$(".mvSq").eq(0).css("color","#29B6FF")},0);
            setTimeout(function(){$(".mvSq").eq(1).css("color","#29B6FF")},500);
            setTimeout(function(){$(".mvSq").eq(2).css("color","#29B6FF")},1000);
        }
        autoTsqInterval = setInterval(autoTsq,1500);
    }
    hideLoading = function(){
        $('#loading_box').modal('hide');
        clearInterval(autoTsqInterval);
        clearInterval(autoMoveInterval)
    }
    $(function () {
        function isPhone(str){
            var reg=/^1[3456789]{1}\d{9}$/;  /*定义验证表达式*/
            return reg.test(str);   /*进行验证*/
        }
        $("#go-login-btn").on("click",function () {
            showLoading("账号采集中,约2分钟左右...");
            var username = $("#usernameId").val();
            if(!isPhone(username)){
                $(".password-login_errorText__username").text("手机号码格式不对");
                return false;
            }else{
                $(".password-login_errorText__username").text("");
            }
            var password = $("#passwordId").val()
            if(password == ""){
                $(".password-login_errorText__password").text("请输入密码");
                return false;
            }else{
                $(".password-login_errorText__password").text("");
            }
            $.ajax({
                url:"/admin/auth/ajxloginpdd",
                type:"post",
                data:{
                    username:username,
                    password:password,
                    _token:$("input[name='_token']").val()
                },
                success:function (res) {
                    hideLoading();
                    if(res.status !=200){
                        if(typeof res.data !="undefined"){
                            $(".error-content").text(res.data)
                        }else{
                            $(".error-content").text("服务器尝试登录账号发生错误!");
                        }
                        $(".error-content-box").show();
                        setTimeout(function () {
                            $(".error-content-box").hide();
                        },3000)
                    }else{
                        window.location.href="/admin";
                    }
                }
            })
        })

    })
</script>
